import React, { useEffect, useState } from 'react';
import { OrgChart } from './OrgChart';
import { Organization } from './types';
import { getDesignTokens } from './theme/tokens';
import { Button } from './components/Button';

export const App: React.FC = () => {
    const [orgData, setOrgData] = useState<Organization | null>(null);
    const [isDarkMode, setIsDarkMode] = useState(false);
    const designTokens = getDesignTokens(isDarkMode);

    useEffect(() => {
        // Load and parse JSON file
        fetch('/orgChart.json')
            .then(response => response.json())
            .then(data => {
                setOrgData(data);
            })
            .catch(error => {
                console.error('Error loading organization data:', error);
            });
    }, []);

    useEffect(() => {
        // Apply dark mode to the entire application
        document.body.style.backgroundColor = designTokens.colors.appBackground;
        document.body.style.transition = 'background-color 0.3s ease';
    }, [isDarkMode, designTokens.colors.appBackground]);

    if (!orgData) return <div>Loading...</div>;

    return (
        <div style={{
            minHeight: '100vh',
            backgroundColor: designTokens.colors.appBackground,
            transition: 'background-color 0.3s ease'
        }}>
            <div style={{
                maxWidth: '1600px',
                margin: '0 auto',
                padding: '0 20px',
                width: '100%',
                boxSizing: 'border-box'
            }}>
                <div style={{ 
                    padding: '20px 0', 
                    display: 'flex',
                    justifyContent: 'space-between',
                    alignItems: 'center'
                }}>
                    <h1 style={{
                        margin: 0,
                        color: designTokens.colors.neutralForeground1Rest,
                        fontSize: '28px',
                        fontWeight: 600,
                        fontFamily: 'Segoe UI, sans-serif',
                        transition: 'color 0.3s ease'
                    }}>
                        Threat Protection & Platform UX
                    </h1>
                    <Button
                        variant="subtle"
                        onClick={() => setIsDarkMode(!isDarkMode)}
                        isDarkMode={isDarkMode}
                    >
                        {isDarkMode ? '‚òÄÔ∏è Light Mode' : 'üåô Dark Mode'}
                    </Button>
                </div>
                <OrgChart organization={orgData} isDarkMode={isDarkMode} />
            </div>
        </div>
    );
};
