import type { Theme } from './types';

export const getDesignTokens = (isDarkMode: boolean): Theme => ({
  colors: {
    appBackground: isDarkMode ? '#292929' : '#ffffff',
    // Brand colors
    brandBackgroundRest: '#0360B9',
    brandBackgroundSelected: '#0044B9',
    brandBackgroundPressed: '#002A6D',
    
    // Neutral backgrounds
    neutralBackground1Rest: isDarkMode ? '#292929' : '#f5f5f5', // Page background
    neutralBackground2Rest: isDarkMode ? '#1f1f1f' : '#fafafa',
    neutralBackground2Selected: isDarkMode ? '#404040' : '#EBEBEB',
    neutralBackground2Pressed: isDarkMode ? '#1a1a1a' : '#E0E0E0',
    neutralBackground3Rest: isDarkMode ? '#404040' : '#ffffff', // Input background
    
    // Neutral foregrounds
    neutralForeground1Rest: isDarkMode ? '#ffffff' : '#242424',
    neutralForegroundInvertedRest: isDarkMode ? '#242424' : '#ffffff',
    
    // Avatar specific
    avatarBackground: isDarkMode ? '#404040' : '#E6E6E6',
    avatarForeground: isDarkMode ? '#ffffff' : '#242424',
    
    // UI specific
    buttonBackground: isDarkMode ? '#333' : '#ddd',
    errorText: '#ff4444',
    inputBorder: isDarkMode ? '#666' : '#ccc',
    overlay: 'rgba(0, 0, 0, 0.5)',
    menuShadow: isDarkMode 
      ? '0 4px 8px rgba(0, 0, 0, 0.5)' 
      : '0 4px 8px rgba(0, 0, 0, 0.1)',
    button: {
      primary: {
        background: {
          rest: isDarkMode ? '#0360B9' : '#0360B9',
          hover: isDarkMode ? '#0078D4' : '#0078D4',
          pressed: isDarkMode ? '#004C87' : '#004C87',
          disabled: isDarkMode ? 'rgba(3, 96, 185, 0.3)' : 'rgba(3, 96, 185, 0.3)',
        },
        foreground: {
          rest: '#FFFFFF',
          hover: '#FFFFFF',
          pressed: '#FFFFFF',
          disabled: isDarkMode ? 'rgba(255, 255, 255, 0.5)' : 'rgba(255, 255, 255, 0.5)',
        },
        border: {
          rest: 'transparent',
          hover: 'transparent',
          pressed: 'transparent',
          disabled: 'transparent',
        },
      },
      secondary: {
        background: {
          rest: isDarkMode ? '#1F1F1F' : '#FFFFFF',
          hover: isDarkMode ? '#292929' : '#F5F5F5',
          pressed: isDarkMode ? '#292929' : '#E0E0E0',
          disabled: isDarkMode ? '#1F1F1F' : '#FFFFFF',
        },
        foreground: {
          rest: isDarkMode ? '#FFFFFF' : '#242424',
          hover: isDarkMode ? '#FFFFFF' : '#242424',
          pressed: isDarkMode ? '#FFFFFF' : '#242424',
          disabled: isDarkMode ? 'rgba(255, 255, 255, 0.5)' : 'rgba(36, 36, 36, 0.5)',
        },
        border: {
          rest: isDarkMode ? '#404040' : '#D1D1D1',
          hover: isDarkMode ? '#404040' : '#D1D1D1',
          pressed: isDarkMode ? '#404040' : '#D1D1D1',
          disabled: isDarkMode ? 'rgba(64, 64, 64, 0.5)' : 'rgba(209, 209, 209, 0.5)',
        },
      },
      subtle: {
        background: {
          rest: 'transparent',
          hover: isDarkMode ? 'rgba(255, 255, 255, 0.06)' : 'rgba(0, 0, 0, 0.06)',
          pressed: isDarkMode ? 'rgba(255, 255, 255, 0.03)' : 'rgba(0, 0, 0, 0.03)',
          disabled: 'transparent',
        },
        foreground: {
          rest: isDarkMode ? '#FFFFFF' : '#242424',
          hover: isDarkMode ? '#FFFFFF' : '#242424',
          pressed: isDarkMode ? '#FFFFFF' : '#242424',
          disabled: isDarkMode ? 'rgba(255, 255, 255, 0.5)' : 'rgba(36, 36, 36, 0.5)',
        },
      },
    },
  },
  
  spacing: {
    l: '24px',
    m: '16px',
    s: '8px',
    horizontalXS: '4px',
    horizontalS: '8px',
    verticalXS: '4px',
    verticalS: '8px',
  },
  
  borderRadius: {
    card: '8px',
    image: '4px',
    input: '4px',
    button: '4px',
  },
  
  typography: {
    title1: {
      fontFamily: '"Segoe UI", sans-serif',
      fontWeight: 600,
      fontSize: '20px',
      lineHeight: '28px',
    },
    h1: {
      fontFamily: '"Segoe UI", sans-serif',
      fontWeight: 600,
      fontSize: '32px',
      lineHeight: '40px',
    },
    h2: {
      fontFamily: '"Segoe UI", sans-serif',
      fontWeight: 600,
      fontSize: '24px',
      lineHeight: '32px',
    },
    subtitle: {
      fontFamily: '"Segoe UI", sans-serif',
      fontWeight: 500,
      fontSize: '18px',
      lineHeight: '24px',
    },
    body1Strong: {
      fontFamily: '"Segoe UI", sans-serif',
      fontWeight: 600,
      fontSize: '14px',
      lineHeight: '20px',
    },
    body1: {
      fontFamily: '"Segoe UI", sans-serif',
      fontWeight: 400,
      fontSize: '14px',
      lineHeight: '20px',
    },
  },
  
  shadows: {
    card: isDarkMode
      ? '0px 0.3px 0.9px 0px rgba(0,0,0,0.2), 0px 1.6px 3.6px 0px rgba(0,0,0,0.28)'
      : '0px 0.3px 0.9px 0px rgba(0,0,0,0.1), 0px 1.6px 3.6px 0px rgba(0,0,0,0.13)',
  },
});
