import React, { useState, useLayoutEffect } from 'react';
import { Organization } from './types';
import { AvatarCard } from './components/AvatarCard';
import { ContextMenu, CONTEXT_MENU_WIDTH } from './components/ContextMenu';
import { getDesignTokens } from './theme/tokens';

// Constants for menu positioning
const MENU_GAP = 8; // gap between card and menu

interface OrgChartProps {
    organization: Organization;
    isDarkMode?: boolean;
    onSelectionChange?: (selectedId: string | null) => void;
}

export const OrgChart: React.FC<OrgChartProps> = ({ 
    organization, 
    isDarkMode = false,
    onSelectionChange 
}) => {
    const [selectedId, setSelectedId] = useState<string | null>(null);
    const [menuPosition, setMenuPosition] = useState<{ x: number; y: number } | null>(null);
    const designTokens = getDesignTokens(isDarkMode);

    const calculateMenuPosition = (element: HTMLElement) => {
        const rect = element.getBoundingClientRect();
        const viewportWidth = window.innerWidth;
        
        // Check if there's enough space on the right
        const spaceOnRight = viewportWidth - rect.right;
        const willFitOnRight = spaceOnRight >= (CONTEXT_MENU_WIDTH + MENU_GAP);

        return {
            x: willFitOnRight ? rect.right + MENU_GAP : rect.left - CONTEXT_MENU_WIDTH - MENU_GAP,
            y: rect.top,
        };
    };

    // Update menu position on window resize
    useLayoutEffect(() => {
        if (selectedId) {
            const element = document.getElementById(selectedId);
            if (element) {
                setMenuPosition(calculateMenuPosition(element));
            }
        }
    }, [selectedId, window.innerWidth]);

    const handleSelect = (id: string) => {
        const newSelectedId = id === selectedId ? null : id;
        setSelectedId(newSelectedId);
        onSelectionChange?.(newSelectedId);

        if (newSelectedId) {
            const element = document.getElementById(id);
            if (element) {
                setMenuPosition(calculateMenuPosition(element));
            }
        } else {
            setMenuPosition(null);
        }
    };

    const getSelectedPerson = () => {
        if (!selectedId) return null;

        const [type, teamIndex, memberIndex] = selectedId.split('-');
        const team = organization.teams[parseInt(teamIndex)];
        
        if (type === 'manager') {
            return {
                ...team.manager,
                role: 'Manager' as const
            };
        } else {
            return {
                ...team.members[parseInt(memberIndex)],
                role: 'IC' as const
            };
        }
    };

    const selectedPerson = getSelectedPerson();

    return (
        <div style={{
            width: '100%',
            display: 'flex',
            justifyContent: 'center',
            position: 'relative',
        }}>
            <div style={{
                display: 'grid',
                gridTemplateColumns: `repeat(auto-fill, minmax(${designTokens.layout.cardMinWidth}, max-content))`,
                gap: designTokens.layout.columnGap,
                maxWidth: '100%',
                justifyContent: 'center',
            }}>
                {organization.teams.map((team, teamIndex) => (
                    <div key={teamIndex} style={{
                        display: 'flex',
                        flexDirection: 'column',
                        gap: designTokens.layout.rowGap,
                        width: 'fit-content',
                    }}>
                        {/* Manager Card */}
                        <div id={`manager-${teamIndex}`}>
                            <AvatarCard
                                id={`manager-${teamIndex}`}
                                name={team.manager.name}
                                title={team.manager.title}
                                imageUrl={team.manager.imageUrl}
                                role="Manager"
                                isDarkMode={isDarkMode}
                                isSelected={selectedId === `manager-${teamIndex}`}
                                onSelect={handleSelect}
                            />
                        </div>

                        {/* Team Members */}
                        <div style={{
                            display: 'flex',
                            flexDirection: 'column',
                            gap: designTokens.layout.rowGap,
                        }}>
                            {team.members.map((member, memberIndex) => (
                                <div key={memberIndex} id={`member-${teamIndex}-${memberIndex}`}>
                                    <AvatarCard
                                        id={`member-${teamIndex}-${memberIndex}`}
                                        name={member.name}
                                        title={member.title}
                                        imageUrl={member.imageUrl}
                                        role="IC"
                                        isDarkMode={isDarkMode}
                                        isSelected={selectedId === `member-${teamIndex}-${memberIndex}`}
                                        onSelect={handleSelect}
                                    />
                                </div>
                            ))}
                        </div>
                    </div>
                ))}
            </div>

            {/* Context Menu */}
            {selectedPerson && menuPosition && (
                <ContextMenu
                    person={selectedPerson}
                    isDarkMode={isDarkMode}
                    position={menuPosition}
                    onClose={() => {
                        setSelectedId(null);
                        setMenuPosition(null);
                        onSelectionChange?.(null);
                    }}
                />
            )}
        </div>
    );
};
