import React from 'react';
import { getDesignTokens } from '../theme/tokens';

// Export the menu width constant
export const CONTEXT_MENU_WIDTH = 280;

interface PersonDetails {
  name: string;
  title?: string;
  imageUrl?: string;
  role: 'Manager' | 'IC';
}

interface ContextMenuProps {
  person: PersonDetails;
  isDarkMode?: boolean;
  onClose?: () => void;
  position: {
    x: number;
    y: number;
  };
}

export const ContextMenu: React.FC<ContextMenuProps> = ({
  person,
  isDarkMode = false,
  onClose,
  position,
}) => {
  const designTokens = getDesignTokens(isDarkMode);
  const menuRef = React.useRef<HTMLDivElement>(null);

  // Handle clicking outside the menu to close it
  React.useEffect(() => {
    const handleClickOutside = (event: MouseEvent) => {
      if (menuRef.current && !menuRef.current.contains(event.target as Node)) {
        onClose?.();
      }
    };

    document.addEventListener('mousedown', handleClickOutside);
    return () => {
      document.removeEventListener('mousedown', handleClickOutside);
    };
  }, [onClose]);

  return (
    <div
      ref={menuRef}
      style={{
        position: 'fixed',
        left: position.x,
        top: position.y,
        backgroundColor: isDarkMode ? designTokens.colors.neutralBackground1Rest : designTokens.colors.neutralBackground2Rest,
        borderRadius: designTokens.borderRadius.large,
        boxShadow: designTokens.shadows.card,
        width: CONTEXT_MENU_WIDTH,
        zIndex: 1000,
        overflow: 'hidden',
      }}
    >
      {/* Image Container */}
      <div style={{
        width: '100%',
        display: 'flex',
        justifyContent: 'center',
        alignItems: 'center',
        padding: designTokens.spacing.m,
        backgroundColor: isDarkMode ? designTokens.colors.neutralBackground2Rest : designTokens.colors.neutralBackground1Rest,
      }}>
        <div style={{
          width: '72px',
          height: '72px',
          borderRadius: designTokens.borderRadius.medium,
          overflow: 'hidden',
          display: 'flex',
          justifyContent: 'center',
          alignItems: 'center',
        }}>
          {person.imageUrl ? (
            <img
              src={person.imageUrl}
              alt={`${person.name}'s avatar`}
              style={{
                width: '100%',
                height: '100%',
                objectFit: 'cover',
                display: 'flex',
              }}
            />
          ) : (
            <div style={{
              width: '100%',
              height: '100%',
              backgroundColor: designTokens.colors.avatarBackground,
              display: 'flex',
              alignItems: 'center',
              justifyContent: 'center',
              color: designTokens.colors.avatarForeground,
              ...designTokens.typography.body1Strong,
              fontSize: '24px',
            }}>
              {person.name.split(' ').map(part => part[0]).join('').toUpperCase().slice(0, 2)}
            </div>
          )}
        </div>
      </div>

      {/* Content */}
      <div style={{ padding: designTokens.spacing.m }}>
        <div style={{
          ...designTokens.typography.body1Strong,
          color: designTokens.colors.neutralForeground1Rest,
          marginBottom: designTokens.spacing.xxs,
        }}>
          {person.name}
        </div>
        {person.title && (
          <div style={{
            ...designTokens.typography.body1,
            color: designTokens.colors.neutralForeground1Rest,
            marginBottom: designTokens.spacing.s,
          }}>
            {person.title}
          </div>
        )}
        <div style={{
          ...designTokens.typography.body1,
          fontSize: '12px',
          color: designTokens.colors.neutralForeground1Rest,
          backgroundColor: person.role === 'Manager' ? designTokens.colors.brandBackgroundRest : designTokens.colors.neutralBackground3Rest,
          padding: `${designTokens.spacing.xxs} ${designTokens.spacing.xs}`,
          borderRadius: designTokens.borderRadius.small,
          display: 'inline-block',
        }}>
          {person.role}
        </div>
      </div>
    </div>
  );
};
