import React from 'react';
import { useColors } from '../theme/useColors';
import { designTokens } from '../theme/designTokens';

interface ButtonProps extends React.ButtonHTMLAttributes<HTMLButtonElement> {
  variant?: 'primary' | 'secondary' | 'subtle';
  isSelected?: boolean;
}

export const Button: React.FC<ButtonProps> = ({
  variant = 'primary',
  isSelected = false,
  children,
  ...props
}) => {
  const colors = useColors();
  const [isHovered, setIsHovered] = React.useState(false);
  const [isPressed, setIsPressed] = React.useState(false);
  const [isFocused, setIsFocused] = React.useState(false);

  const getBackgroundColor = () => {
    if (props.disabled) {
      return colors.neutral.background.disabled;
    }

    if (variant === 'primary') {
      if (isPressed) return colors.brand.background.pressed;
      if (isHovered) return colors.brand.background.hover;
      if (isSelected) return colors.brand.background.selected;
      return colors.brand.background.rest;
    }
    
    if (isPressed) return colors.neutral.background[1].pressed;
    if (isHovered) return colors.neutral.background[1].hover;
    if (isSelected) return colors.neutral.background[1].selected;
    return colors.neutral.background[1].rest;
  };

  const getForegroundColor = () => {
    if (props.disabled) {
      return colors.neutral.foreground.disabled;
    }

    if (variant === 'primary') {
      return colors.neutral.foreground.onBrand;
    }

    if (isPressed) return colors.brand.foreground[2].pressed;
    if (isHovered) return colors.brand.foreground[2].hover;
    if (isSelected) return colors.brand.foreground[2].rest;
    return colors.brand.foreground[2].rest;
  };

  const getBorderColor = () => {
    if (props.disabled) {
      return colors.neutral.stroke.disabled;
    }

    if (variant === 'primary') {
      if (isPressed) return colors.brand.stroke[2].pressed;
      if (isHovered) return colors.brand.stroke[2].hover;
      if (isSelected) return colors.brand.stroke[2].rest;
      return colors.brand.stroke[2].rest;
    }

    if (isPressed) return colors.neutral.stroke[1].pressed;
    if (isHovered) return colors.neutral.stroke[1].hover;
    if (isSelected) return colors.neutral.stroke[1].selected;
    return colors.neutral.stroke[1].rest;
  };

  const getFocusStyles = () => {
    if (!isFocused) return {};

    return {
      outline: 'none',
      boxShadow: `0 0 0 2px ${colors.neutral.stroke.focus[1]}, 0 0 0 4px ${colors.neutral.stroke.focus[2]}`,
    };
  };

  return (
    <button
      {...props}
      style={{
        padding: `${designTokens.spacing.vertical.s} ${designTokens.spacing.horizontal.m}`,
        backgroundColor: getBackgroundColor(),
        color: getForegroundColor(),
        border: variant === 'subtle' ? 'none' : `1px solid ${getBorderColor()}`,
        borderRadius: designTokens.borderRadius.medium,
        cursor: props.disabled ? 'not-allowed' : 'pointer',
        ...designTokens.typography.body1.regular,
        transition: 'all 0.2s ease',
        margin: `0 ${designTokens.spacing.horizontal.xs}`,
        ...getFocusStyles(),
        ...props.style,
      }}
      onMouseEnter={() => setIsHovered(true)}
      onMouseLeave={() => {
        setIsHovered(false);
        setIsPressed(false);
      }}
      onMouseDown={() => setIsPressed(true)}
      onMouseUp={() => setIsPressed(false)}
      onFocus={() => setIsFocused(true)}
      onBlur={() => {
        setIsFocused(false);
        setIsHovered(false);
        setIsPressed(false);
      }}
    >
      {children}
    </button>
  );
};
