import React from 'react';
import { getDesignTokens } from '../theme/tokens';

// Types for the component props
export interface AvatarCardProps {
  name: string;
  title?: string;
  imageUrl?: string;
  initials?: string;
  role?: 'Manager' | 'IC';
  className?: string;
  isDarkMode?: boolean;
  isSelected?: boolean;
  onSelect?: (id: string) => void;
  id: string;
}

const getInitials = (name: string): string => {
  return name
    .split(' ')
    .map(part => part[0])
    .join('')
    .toUpperCase()
    .slice(0, 2);
};

const AvatarImage: React.FC<{
  imageUrl?: string;
  initials?: string;
  name: string;
  isDarkMode?: boolean;
}> = ({ imageUrl, initials, name, isDarkMode = false }) => {
  const designTokens = getDesignTokens(isDarkMode);
  const displayInitials = initials || getInitials(name);

  return (
    <div
      style={{
        width: '56px',
        height: '56px',
        backgroundColor: designTokens.colors.avatarBackground,
        borderRadius: designTokens.borderRadius.medium,
        display: 'flex',
        alignItems: 'center',
        justifyContent: 'center',
        color: designTokens.colors.avatarForeground,
        overflow: 'hidden',
        transition: designTokens.transitions.default,
      }}
      role="img"
      aria-label={`Avatar for ${name}`}
    >
      {imageUrl ? (
        <img
          src={imageUrl}
          alt={`${name}'s avatar`}
          style={{
            width: '100%',
            height: '100%',
            objectFit: 'cover',
          }}
        />
      ) : (
        <span style={{
          ...designTokens.typography.body1Strong,
          fontSize: '20px',
        }}>
          {displayInitials}
        </span>
      )}
    </div>
  );
};

export const AvatarCard: React.FC<AvatarCardProps> = ({
  name,
  title,
  imageUrl,
  initials,
  role = 'IC',
  className = '',
  isDarkMode = false,
  isSelected = false,
  onSelect,
  id,
}) => {
  const designTokens = getDesignTokens(isDarkMode);
  const isManager = role === 'Manager';

  const getBackgroundColor = () => {
    if (isManager) {
      return isSelected ? designTokens.colors.brandBackgroundSelected : designTokens.colors.brandBackgroundRest;
    } else {
      return isSelected ? designTokens.colors.neutralBackground2Selected : designTokens.colors.neutralBackground2Rest;
    }
  };

  // Create a style tag for the pressed state styles
  React.useEffect(() => {
    const styleId = 'avatar-card-styles';
    let styleTag = document.getElementById(styleId) as HTMLStyleElement;
    
    if (!styleTag) {
      styleTag = document.createElement('style');
      styleTag.id = styleId;
      document.head.appendChild(styleTag);
    }

    const pressedStyles = `
      .avatar-card-manager:active {
        background-color: ${designTokens.colors.brandBackgroundPressed} !important;
      }
      .avatar-card-regular:active {
        background-color: ${designTokens.colors.neutralBackground2Pressed} !important;
      }
    `;

    styleTag.textContent = pressedStyles;

    return () => {
      if (styleTag && styleTag.parentNode) {
        styleTag.parentNode.removeChild(styleTag);
      }
    };
  }, [isDarkMode, designTokens.colors.brandBackgroundPressed, designTokens.colors.neutralBackground2Pressed]);

  const cardClassName = `${className} ${isManager ? 'avatar-card-manager' : 'avatar-card-regular'}`.trim();

  const handleClick = () => {
    onSelect?.(id);
  };

  return (
    <div
      className={cardClassName}
      style={{
        position: 'relative',
        backgroundColor: getBackgroundColor(),
        borderRadius: designTokens.borderRadius.large,
        boxShadow: designTokens.shadows.card,
        display: 'flex',
        flexDirection: 'row',
        alignItems: 'center',
        overflow: 'visible',
        width: '100%',
        minWidth: '240px',
        maxWidth: '248px',
        transition: designTokens.transitions.fast,
        transform: isSelected ? 'scale(1.02)' : 'scale(1)',
        cursor: 'pointer',
        outline: isSelected ? `2px solid ${designTokens.colors.brandBackgroundRest}` : 'none',
        outlineOffset: '2px',
      }}
      onClick={handleClick}
      role="button"
      tabIndex={0}
      aria-pressed={isSelected}
      onKeyDown={(e) => {
        if (e.key === 'Enter' || e.key === ' ') {
          e.preventDefault();
          handleClick();
        }
      }}
    >
      <div style={{ padding: designTokens.spacing.xxs }}>
        <AvatarImage
          imageUrl={imageUrl}
          initials={initials}
          name={name}
          isDarkMode={isDarkMode}
        />
      </div>
      
      <div style={{
        padding: designTokens.spacing.xs,
        minWidth: 0,
        flex: 1,
        overflow: 'hidden'
      }}>
        <div
          style={{
            ...designTokens.typography.body1Strong,
            color: isManager
              ? designTokens.colors.neutralForegroundInvertedRest
              : designTokens.colors.neutralForeground1Rest,
            whiteSpace: 'nowrap',
            overflow: 'hidden',
            textOverflow: 'ellipsis',
            transition: designTokens.transitions.default,
            width: '100%',
          }}
          title={name}
        >
          {name}
        </div>
        {title && (
          <div
            style={{
              ...designTokens.typography.body1,
              color: isManager
                ? designTokens.colors.neutralForegroundInvertedRest
                : designTokens.colors.neutralForeground1Rest,
              whiteSpace: 'nowrap',
              overflow: 'hidden',
              textOverflow: 'ellipsis',
              transition: designTokens.transitions.default,
              width: '100%',
            }}
            title={title}
          >
            {title}
          </div>
        )}
      </div>
    </div>
  );
};

export default AvatarCard;
