import { DefenderDesignTokens } from './types';

export const tokens: DefenderDesignTokens = {
  colors: {
    neutral: {
      background: {
        rest: '#FFFFFF',
        hover: '#F5F5F5',
        pressed: '#E5E5E5',
        selected: '#F0F0F0'
      },
      foreground: {
        primary: '#000000',
        secondary: '#666666',
        tertiary: '#999999'
      },
      stroke: {
        rest: '#E0E0E0',
        hover: '#D0D0D0',
        pressed: '#C0C0C0'
      }
    },
    brand: {
      primary: {
        background: '#0078D4',
        foreground: '#FFFFFF',
        stroke: '#106EBE'
      },
      security: {
        safe: '#107C10',
        warning: '#D83B01',
        critical: '#D13438'
      }
    },
    status: {
      success: '#107C10',
      warning: '#FFB900',
      error: '#D13438',
      info: '#0078D4'
    }
  },
  typography: {
    heading: {
      h1: {
        fontSize: '28px',
        lineHeight: '36px',
        fontWeight: '600'
      },
      h2: {
        fontSize: '24px',
        lineHeight: '32px',
        fontWeight: '600'
      },
      h3: {
        fontSize: '20px',
        lineHeight: '28px',
        fontWeight: '600'
      }
    },
    body: {
      regular: {
        fontSize: '14px',
        lineHeight: '20px',
        fontWeight: '400'
      },
      small: {
        fontSize: '12px',
        lineHeight: '16px',
        fontWeight: '400'
      }
    }
  },
  spacing: {
    xs: '4px',
    sm: '8px',
    md: '16px',
    lg: '24px',
    xl: '32px'
  },
  elevation: {
    low: '0px 2px 4px rgba(0, 0, 0, 0.1)',
    medium: '0px 4px 8px rgba(0, 0, 0, 0.1)',
    high: '0px 8px 16px rgba(0, 0, 0, 0.1)'
  }
};
