import React from 'react';
import { Organization } from './types';
import { AvatarCard } from './components/AvatarCard';
import { getDesignTokens } from './theme/tokens';

interface OrgChartProps {
    organization: Organization;
    isDarkMode?: boolean;
}

export const OrgChart: React.FC<OrgChartProps> = ({ organization, isDarkMode = false }) => {
    const designTokens = getDesignTokens(isDarkMode);

    return (
        <div style={{
            width: '100%'
        }}>
            <div style={{
                display: 'grid',
                gridTemplateColumns: 'repeat(auto-fit, minmax(300px, 1fr))',
                gap: '24px',
                width: '100%',
            }}>
                {organization.teams.map((team, index) => (
                    <div key={index} style={{
                        display: 'flex',
                        flexDirection: 'column',
                        gap: '12px',
                        minWidth: 0, // Prevent content from forcing grid item wider
                    }}>
                        {/* Manager Card */}
                        <AvatarCard
                            name={team.manager.name}
                            title={team.manager.title}
                            imageUrl={team.manager.imageUrl}
                            role="Manager"
                            isDarkMode={isDarkMode}
                        />

                        {/* Team Members */}
                        <div style={{
                            display: 'flex',
                            flexDirection: 'column',
                            gap: '12px',
                        }}>
                            {team.members.map((member, memberIndex) => (
                                <AvatarCard
                                    key={memberIndex}
                                    name={member.name}
                                    title={member.title}
                                    imageUrl={member.imageUrl}
                                    role="IC"
                                    isDarkMode={isDarkMode}
                                />
                            ))}
                        </div>
                    </div>
                ))}
            </div>
        </div>
    );
};
